import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_POKEMONS, GET_POKEMON_DETAILS } from './graphql/graphqlScript';
import Pokemon, { details } from './pokemonDetails';
import Pokemons from './pokemonList';

export function PokemonDetails(props){
    //const setActive = useState("pokeDetails")
    console.log(props)
    //setActive("pokeDetails");
    return(
        //<p>Hello</p>
        <div>
            <h2>Pokemon Details</h2>
            <p>
                {props.name} <img src={props.image} alt={props.name}></img>
            </p>
            <p>Abilities
                {props.abilities.map((ability) => (
                    <p>{ability}</p>
                ))}
            </p>
        </div>
    );
    //const { loading, error, data } = useQuery(GET_POKEMON_DETAILS, {variables:{"name":props.name}});
    //console.log(data);
}

function Main(){
    const [active, setActive] = useState("pokeList");

    return(
        <div>
            {active === "pokeList" && <Pokemons/>}
            {/* {active === "pokeDetails" && <PokemonDetails/>} */}
        </div>
    );
}

export default Main;
