    return(
        <div key={props.pokemon.name} onClick={(e) => PokemonDetails(props.pokemon.name, e)}>
            <p>
                {props.pokemon.name} <img src={props.pokemon.image} alt="pokemon"></img>
            </p>
        </div>
    );
};
