import React from 'react';
import { useQuery, gql } from '@apollo/client';
//import Pokemon from './pokemonDetails';

const pokemons = gql`
query pokemons($limit: Int, $offset: Int) {
  pokemons(limit: $limit, offset: $offset) {
    count
    next
    previous
    status
    message
    results {
      url
      name
      image
    }
  }
}
`;

function Pokemons({onSelectedPokemon}) {
  const { loading, error, data } = useQuery(pokemons);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  console.log(data);
  const details = gql`
    query pokemon($name: String!) {
      pokemon(name: $name) {
        id
        name
        abilities {
          ability {
            name
          }
        }
        moves {
          move {
            name
          }
        }
        types {
          type {
            name
          }
        }
        message
        status
      }
    }
  `;

  function PokemonDetails({name}){
    const { loading, error, data } = useQuery(details, {
      variables : {name}, 
    });
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    return (
      console.log(data)
      // <div>
      //   <p>{data.pokemon.name}</p>
      //   <p>{data.pokemon.abilities}</p>
      // </div>
    );
  };

  return (
    <div onClick={onSelectedPokemon}>
      {data.pokemons.results.map(pokemon => (
        <p key={pokemon.name} onClick={PokemonDetails(`${pokemon.name}`)}>
          {pokemon.name} <img src={pokemon.image} alt="pokemon"></img>
        </p>
      ))}
    </div>
  );

  // return data.pokemons.results.map(({ url, name, image }) => (
  //   <div key={name}>
  //     <p>
  //       {name} <img src={image} alt="pokemon"></img> 
  //     </p>
  //   </div>
  // ));

  // return data.pokemons.results.map((currentPokemon) => (
  //   <Pokemon pokemon={currentPokemon} />
  // ));
}

export default Pokemons;
