import React, { useState } from 'react';
import { useQuery } from '@apollo/client';
import { GET_POKEMONS, GET_POKEMON_DETAILS } from './graphql/graphqlScript';

function Main(){
    const [active, setActive] = useState("pokeList");
    function GetPokemons(){
        setActive("pokeList");
        const { loading, error, data } = useQuery(GET_POKEMONS);
        if(loading) return <p>Loading...</p>
        if(error) return <p>Error :(</p>
        console.log(data);

        return (
            <div>
                {data.pokemons.results.map(pokemon => (
                    <p key={pokemon.url} onClick={(e) => PokemonDetails(pokemon, e)}>
                        {pokemon.name} <img src={pokemon.image} alt={pokemon.name}></img>
                    </p>
                ))}
            </div>
        );
    }

    function PokemonDetails(props){
        setActive("pokeDetails");
        return(
            console.log(props)
            // <div>
            //     <h2>Pokemon Details</h2>
            //     <p>
            //         {props.name} <img src={props.image} alt={props.name}></img>
            //     </p>
            // </div>
        );
        //const { loading, error, data } = useQuery(GET_POKEMON_DETAILS, {variables:{"name":props.name}});
        //console.log(data);
    }
    return(
        <div>
            {active === "pokeList" && <GetPokemons/>}
            {active === "pokeDetails" && <PokemonDetails/>}
        </div>
    );
}

export default Main;
