import React, { useState } from 'react';
import { useQuery, useLazyQuery } from '@apollo/client';
import { GET_POKEMON_DETAILS } from './graphql/graphqlScript';
import {PokemonDetails} from './main';

export var details;

function Pokemon(props){
    const [getDetails, { loading, error, data }] = useLazyQuery(GET_POKEMON_DETAILS, {variables:{"name":props.pokemon.name}});
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    if (data){
        details = JSON.parse(JSON.stringify(data.pokemon));
        details.image = props.pokemon.image;
        console.log(details);
        return(
            // <div key={details.id}>
            //     <h2>Pokemon Details</h2>
            //     <img src={details.image} alt={details.name}/>
            //     <p>Name: {details.name}</p>
            //     <p>Abilities: {details.abilities.map((ab) => {
            //         <p>{ab.ability.name}</p>
            //     })}</p>
            // </div>
            details.abilities.map((ab) => {
                console.log(ab);
            })
        );
    }

    console.log(data);
    console.log(props);

    return(
        <div key={props.pokemon.name}>
            <p>
                {props.pokemon.name} <img src={props.pokemon.image} alt="pokemon"></img>
            </p>
            <button onClick={() => getDetails()}>See Pokemon Details</button>
        </div>
    );
};
export default Pokemon;
