import React from 'react';
import { Query } from 'react-apollo';
import gql from 'graphql-tag';

function Courses() {
    <Query query={gql`
        query GetRates {
            rates(currency: "USD") {
            currency
            }
        }
    `}>
        {({loading, error, data}) => {
            if(loading) return <p>Loading...</p>;
            if(error) return <p>Error</p>
            console.log(data);
        }}
    </Query>
};

export default Courses;
