function MyPokemons(){
    //Get list of caught Pokemons
var myPokemons = JSON.parse(window.localStorage.getItem("myPokemons"));
    var [myPokemonsList, setList] = useState([]);
    myPokemonsList = [...myPokemons];
    function ReleasePokemon(index){
        var confirm = window.confirm("Are you sure you want to release this Pokemon?");
        if(confirm){
            myPokemons.splice(index, 1);
            setList(myPokemonsList => [...myPokemons]);
            //console.log(myPokemonsList);
            window.localStorage.setItem("myPokemons", JSON.stringify(myPokemons));
        }
    }

    if (myPokemonsList!=null){ //If list is not empty return list
        return(
            myPokemonsList.map((mp, index) => (
                <div key={mp.nickname}>
                    <p>Index: {index}</p>
                    <h1>{mp.name}</h1>
                    <img src={mp.image} alt={mp.name}/>
                    <p>Nickname: {mp.nickname}</p>
                    <button onClick={ReleasePokemon.bind(this, index)}>Release Pokemon</button>
                </div>
            ))
        );
    }else{
        return <p>No Pokemons caught yet:(</p>
    }
};

export default MyPokemons;
