import React, { useState } from 'react';
import { ApolloClient, InMemoryCache, ApolloProvider } from '@apollo/client';
import Pokemons from './Pokemons';
import Pokemon from './pokemonDetails';
import './App.css';

const client = new ApolloClient({
  uri: "https://graphql-pokeapi.vercel.app/api/graphql",
  cache: new InMemoryCache()
});

function App() {
  const [active, setActive] = useState("pokeList");

  return (
    <ApolloProvider client={client}>
      {/* <Main/> */}
      {/* <Pokemons/> */}
      <nav>
        <button onClick={function(){
          setActive("pokeList")
        }}>Pokemon List</button>
        {/* <button onClick={function(){
          setActive("pokeDetails")
        }}>Pokemon Details</button>
        <button onClick={function(){
          setActive("myPokemons")
        }}>My Pokemons</button> */}
      </nav>
      <div>
        {active === "pokeList" && <Pokemons/>}
        {/* {active === "pokeDetails" && <Pokemon/>} */}
        {/* {active === "myPokemons" && <Pokemons/>} */}
      </div>

    </ApolloProvider>
  );
}

export default App;
