import React from 'react';
import { useQuery } from '@apollo/client';
import { GET_POKEMON_DETAILS } from './graphql/graphqlScript';

export var details;

export function PokemonDetails(pokemonDetails){
    // const pokeName = name;
    // const { loading, error, data } = useQuery(GET_POKEMON_DETAILS, {
    //   variables : {name}, 
    // });
    // if (loading) return <p>Loading...</p>;
    // if (error) return <p>Error :(</p>;

    return (
    //console.log(pokemonDetails)
      <div>
          <p>{pokemonDetails}</p>
      </div>
    );
};

export function Pokemon(props){
    const { loading, error, data } = useQuery(GET_POKEMON_DETAILS, {variables:{"name":props.pokemon.name}});
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    details = JSON.parse(JSON.stringify(data.pokemon));
    details.image = props.pokemon.image;
    console.log(details);
    console.log(props);

    return(
        <div key={props.pokemon.name} onClick={(e) => PokemonDetails(details, e)}>
            <p>
                {props.pokemon.name} <img src={props.pokemon.image} alt="pokemon"></img>
            </p>
        </div>
    );
};
//export default Pokemon;
