import React, { useState } from 'react';
import { useQuery, useLazyQuery } from '@apollo/client';
import { GET_POKEMON_DETAILS } from './graphql/graphqlScript';
import {PokemonDetails} from './main';

export var details;

function Pokemon(props){
    const [getDetails, { loading, error, data }] = useLazyQuery(GET_POKEMON_DETAILS, {variables:{"name":"ivysaur"}});
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    details = JSON.parse(JSON.stringify(data.pokemon));
    details.image = props.pokemon.image;
    console.log(details);
    //console.log(props);

    return(
        <div key={props.pokemon.name}>
            <p>
                {props.pokemon.name} <img src={props.pokemon.image} alt="pokemon"></img>
            </p>
            <button onClick={() => getDetails()}>See Pokemon Details</button>
        </div>
    );
};
export default Pokemon;
