import React from 'react';
import { useQuery, gql } from '@apollo/client';

const details = gql`
    query pokemon($name: String!) {
      pokemon(name: $name) {
        id
        name
        abilities {
          ability {
            name
          }
        }
        moves {
          move {
            name
          }
        }
        types {
          type {
            name
          }
        }
        message
        status
      }
    }
  `;

  function PokemonDetails({name}){
    const { loading, error, data } = useQuery(details, {
      variables : {name}, 
    });
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;

    return (
      console.log(data)
      // <div>
      //   <p>{data.pokemon.name}</p>
      //   <p>{data.pokemon.abilities}</p>
      // </div>
    );
  };

export default PokemonDetails;
