import React from 'react';

//Get list of caught Pokemons
var myPokemons;

function ReleasePokemon(index){
    myPokemons = JSON.parse(window.localStorage.getItem("myPokemons"));
    var confirm = window.confirm("Are you sure you want to release this Pokemon?");
    if(confirm){
        var tempList = myPokemons;
        tempList.splice(index, 1);
        window.localStorage.setItem("myPokemons", JSON.stringify(tempList));
        window.location.reload(false);
    }
};

function MyPokemons(){
    myPokemons = JSON.parse(window.localStorage.getItem("myPokemons"));
    if (myPokemons!=null){ //If list is not empty return list
        return(
            myPokemons.map((mp, index) => (
                <div key={mp.nickname}>
                    <p>Index: {index}</p>
                    <h1>{mp.name}</h1>
                    <img src={mp.image} alt={mp.name}/>
                    <p>Nickname: {mp.nickname}</p>
                    <button onClick={ReleasePokemon.bind(this, index)}>Release Pokemon</button>
                </div>
            ))
        );
    }else{
        return (
            <p>No Pokemons caught yet:(</p>
        );
    }
};

export default MyPokemons;
