import React from 'react';
import { useQuery, gql } from '@apollo/client';

const details = gql`
    query pokemon($name: String!) {
      pokemon(name: $name) {
        id
        name
        abilities {
          ability {
            name
          }
        }
        moves {
          move {
            name
          }
        }
        types {
          type {
            name
          }
        }
        message
        status
      }
    }
  `;

function Pokemon(props){
    const { loading, error, data } = useQuery(details, {variables:{"name":props.pokemon.name}});
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    console.log(data);
    console.log(props);

    function PokemonDetails({name}){
        const { loading, error, data } = useQuery(details, {
          variables : {name}, 
        });
        if (loading) return <p>Loading...</p>;
        if (error) return <p>Error :(</p>;

        return (
          console.log(data)
          // <div>
          //   <p>{data.pokemon.name}</p>
          //   <p>{data.pokemon.abilities}</p>
          // </div>
        );
    };

    return(
        <div key={props.pokemon.name} onClick={(e) => PokemonDetails(props.pokemon.name, e)}>
            <p>
                {props.pokemon.name} <img src={props.pokemon.image} alt="pokemon"></img>
            </p>
        </div>
    );
};
export default Pokemon;
