import React from 'react';
import { useQuery } from '@apollo/client';
import Pokemon from './pokemonDetails';
import { GET_POKEMONS } from './graphql/graphqlScript';
import { GET_POKEMON_DETAILS } from './graphql/graphqlScript';


function Pokemons({onPokemonSelected}) {
  const { loading, error, data } = useQuery(GET_POKEMONS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  console.log(data);


  // return (
  //   <div>
  //     {data.pokemons.results.map(pokemon => (
  //       <p key={pokemon.name}>
  //         {pokemon.name} <img src={pokemon.image} alt="pokemon"></img>
  //       </p>
  //     ))}
  //   </div>
  // );

  // return data.pokemons.results.map((currentPokemon) => (
  //   <Pokemon pokemon={currentPokemon} key={currentPokemon.url}/>
  // ));

  return (
    <select name="pokemon" onChange={onPokemonSelected}>
      {data.pokemons.results.map(pokemon => (
        <option key={pokemon.url} value={pokemon.name}>
          {pokemon.name}
        </option>
      ))}
    </select>
  );

  function PokemonPhoto ({name}){
    const { loading, error, data } = useQuery(GET_POKEMON_DETAILS, {
      variables: { name },
    });

    if (loading) return null;
    if (error) return `Error! ${error}`;

    return (
      <img src={data.pokemon.image} style={{ height: 100, width: 100 }} />
    );
  }
}

export default Pokemons;
