import React from 'react';
import { useLazyQuery } from '@apollo/client';
import { GET_POKEMON_DETAILS } from './graphql/graphqlScript';

var details;
var caughtPokemons = []; //Initialise empty list of caught pokemons

function Probability(n){ //Random number generator
    return Math.floor(Math.random() * Math.floor(n));
};

function CatchPokemon(pokemon){ //Catch Pokemon function
    var chance = Probability(2); //Generate random number between 0 and 1 for the 50% chance of catching a pokemon
    var pokemonsList = JSON.parse(window.localStorage.getItem("myPokemons"));
    var existingNicknames = [];
    existingNicknames.length =0;
    if(chance==0){ //If Probability returns 0, pokemon is not caught 
        return alert("Oops, better luck next time!");
    } else{
        var promptNickname = prompt("Congratulations! You caught the Pokemon!\nGive it a nickname: ");
        if (promptNickname!=null || promptNickname!=""){
            //pokemonsList.map(p => existingNicknames.push(p.nickname));
            console.log(existingNicknames);
            pokemon.nickname = promptNickname; //Add nickname given by user to the pokemon info provided by API

            if(JSON.parse(window.localStorage.getItem('myPokemons')) != null){ //Check if there is already an existing list of caught pokemons
                var array = JSON.parse(window.localStorage.getItem('myPokemons')); //Get list of caught pokemon
                array.push(pokemon); //Add the newly caught pokemon to the list
                window.localStorage.setItem('myPokemons', JSON.stringify(array)); //Update list
            }else{
                caughtPokemons.push(pokemon);
                window.localStorage.setItem('myPokemons', JSON.stringify(caughtPokemons));
            }
        }
    }
};

function Pokemon(props){
    //Lazy Query to execute only after "See Pokemon Details" button is clicked
    const [getDetails, { loading, error, data }] = useLazyQuery(GET_POKEMON_DETAILS, {variables:{"name":props.pokemon.name}});
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    if (data){
        details = JSON.parse(JSON.stringify(data.pokemon));
        details.image = props.pokemon.image;
        return(
            <div key={details.id}>
                <h1>Pokemon Details</h1>
                <img src={details.image} alt={details.name}/>
                <p>Name: {details.name}</p>
                <p>Types: {details.types.map((t) => {
                    return(
                        <span key={t.type.name}>{t.type.name}, </span>
                    );
                })}</p>
                <p>Moves: {details.moves.map((m) => {
                    return(
                        <span key={m.move.name}>{m.move.name}, </span>
                    );
                })}</p>
                <button onClick={CatchPokemon.bind(this, details)}>Catch Pokemon</button>
            </div>
        );
    }

    return(
        <div key={props.pokemon.name}>
            <h1>
                {props.pokemon.name} <br/><img src={props.pokemon.image} alt="pokemon"></img>
            </h1>
            <button onClick={() => getDetails()}>See Pokemon Details</button>
        </div>
    );
};
export default Pokemon;
