import React from 'react';
import { useQuery, useLazyQuery } from '@apollo/client';
import  Pokemon  from './pokemonDetails';
import { GET_POKEMONS, GET_POKEMON_DETAILS } from './graphql/graphqlScript';


function Pokemons() {
  const { loading, error, data } = useQuery(GET_POKEMONS);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  //console.log(data);

  const [getDetails, {loading2, error2, data2}] = useLazyQuery(GET_POKEMON_DETAILS,
    {variables : {name:"ivysaur" }});

  if(error2) return <p>Error</p>
  if(loading2) return <p>Loading Details...</p>
  console.log(data2);


  return (
    data.pokemons.results.map(pokemon => (
      <div>
        <p key={pokemon.name}>
          {pokemon.name} <img src={pokemon.image} alt={pokemon.name}></img>
        </p>
        <button onClick={() => getDetails()}>See Pokemon Details</button>
      </div>
    ))
  );

  // return data.pokemons.results.map((currentPokemon) => (
  //   <Pokemon pokemon={currentPokemon} key={currentPokemon.url}/>
  // ));

}

export default Pokemons;
